mapper.select:
SELECT product.id,product.brand_id,brand.name brand_name , product.product_no,product.product_name,tech.id tech_id,tech.name tech_name, thickness.id thickness_id,thickness.name thickness_name, manufactor.id manufactor_id,manufactor.name manufactor_name, identify.id identify_id,identify.name identify_name, quantity.id quantity_id,quantity.name quantity_name, product.amount,product.price, ( SELECT COUNT(*) FROM t_product_query_times qt WHERE qt.product_id = product.id
AND qt.create_time >= #{queryTimesStartDate}
AND qt.create_time <= #{queryTimesEndDate}
) query_times, ( select avg(t.price) from t_product_out t where t.product_id = product.id ) avgSalePrice, product.create_time,user.id user_id,user.username user_name FROM t_product product LEFT JOIN t_brand brand ON product.brand_id = brand.id LEFT JOIN t_tech tech ON product.tech_id = tech.id LEFT JOIN t_thickness thickness ON product.thickness_id = thickness.id LEFT JOIN t_manufactor manufactor ON manufactor.id = product.manufactor_id LEFT JOIN t_identify identify ON identify.id = product.identify_id LEFT JOIN t_quantity quantity ON quantity.id = product.quantity_id LEFT JOIN t_user user on user.id = product.create_user_id

mapper.update:
update t_product set 

    amount = amount + #{amount},


	price = #{price}		    

where id = #{id}

mapper.select:
select product.id,product.brand_id,brand.name brand_name , product.product_no,product.product_name,tech.id tech_id,tech.name tech_name, thickness.id thickness_id,thickness.name thickness_name, manufactor.id manufactor_id,manufactor.name manufactor_name, identify.id identify_id,identify.name identify_name, quantity.id quantity_id,quantity.name quantity_name,product.price, product.create_time,user.id user_id,user.username user_name, product.amount FROM t_product product LEFT JOIN t_brand brand ON product.brand_id = brand.id LEFT JOIN t_tech tech ON product.tech_id = tech.id LEFT JOIN t_thickness thickness ON product.thickness_id = thickness.id LEFT JOIN t_manufactor manufactor ON manufactor.id = product.manufactor_id LEFT JOIN t_identify identify ON identify.id = product.identify_id LEFT JOIN t_quantity quantity ON quantity.id = product.quantity_id LEFT JOIN t_user user on user.id = product.create_user_id
product.manufactor_id = #{manufactorId}
product.product_no like CONCAT('%',#{productName},'%') or product.product_name like CONCAT('%',#{productName},'%')
