mapper.select:
		SELECT
			stock.id,product.brand_id,brand.name brand_name,product.id
			product_id,product.product_no,product.product_name,tech.id
			tech_id,tech.name tech_name,thickness.id thickness_id,thickness.name
			thickness_name,stock.amount,product.price,stock.identify_type
			,identify.id identify_id,identify.name identify_name,
			(
				SELECT COUNT(*) FROM t_product_query_times qt 
				WHERE qt.product_id = stock.product_id
				
					
						qt.create_time >= #{queryTimesStartDate}
									    
				
				
					
						qt.create_time <= #{queryTimesEndDate}
									    
				
			) query_times
		FROM t_stock stock
		LEFT JOIN t_product product ON stock.product_id = product.id
		LEFT JOIN t_brand brand ON product.brand_id = brand.id
		LEFT JOIN t_tech tech ON product.tech_id = tech.id
		LEFT JOIN t_thickness thickness ON product.thickness_id = thickness.id
		LEFT JOIN t_identify identify ON stock.identify_id = stock.identify_id
		
   			
				AND stock.product_id = #{productId}
			
			
				AND stock.identify_type = #{identifyType}
			
			
				AND stock.identify_id = #{identifyId}
			
			
				AND stock.manufactor_id = #{manufactorId}
			
			
				AND product.product_no = #{productNo}
			
			
				AND product.product_name = #{productName}
			
		
		order by stock.amount desc

SELECT count(*) from t_stock

	
		product_id = #{productId}
	
	
		AND identify_type = #{identifyType}
	
	
		AND identify_id = #{identifyId}
	
	
		AND manufactor_id = #{manufactorId}

mapper.update:
update T_STOCK set 
	amount = amount + #{amount}

	
	  product_id = #{productId}  
	
	
	  and identify_type = #{identifyType}  
	
	
	  and identify_id = #{identifyId}  
	
	
	  and manufactor_id = #{manufactorId}
